
public class WrapperClasses {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		Wrapper class = provides a way to use primitive data as reference data types
		
		
        // Autoboxing
		Boolean a = true;
		Character b = '@';
		Integer c = 12;
		Double d = 3.14;
		String e = "Bro";
		
		
		if (a) {
			System.out.println("TRUE");
		}
	}

}
